import axios from 'axios';
const url = "https://covid19.mathdro.id/api";

export const fetchData = async() => {
    try {
        const { data: { confirmed, recovered, deaths, lastUpdate } } = await axios.get(url); // specifically getting theses 4 categories of info from the url/api

        return { confirmed, recovered, deaths, lastUpdate };
    } catch (error) {
        console.log("API ERR");
    }
}

