import React, { useEffect, useState } from 'react';
import { Cards, Chart, CountryPicker } from './components';
import styles from './App.module.css';
import { fetchData } from './api';
import coronaImage from './images/image.png';
import { Button, Dialog, DialogContent, DialogTitle } from '@material-ui/core';

function App() {

    // const [data, setData] = useState({
    // })

    const [data, setData] = useState({})
    const [country, setCountry] = useState('')
    const [tips, setTips] = useState(false)

    useEffect(() => {   // fetching from api and setting up the data to send to the jsx files
        async function doTheFetch() {
            const fetchedData = await fetchData();

            setData(fetchedData);
        }
        doTheFetch();
      }, []);

    const handleCountryChange = async(country) => {
        const fetchedData = await fetchData(country);

        setData(fetchedData);
        setCountry(country);
    }

    function openTips() {
        setTips(true);
    }

    function closeTips() {
        setTips(false);
    }

    return (
        <div className={styles.container}>  
            <img className={styles.image} src={coronaImage} alt="COVID-19"/>
            <Cards data={data}/>
            <Button onClick={openTips} variant="contained" color="primary">How to Stay Safe</Button>
            <Dialog open={tips} onClose={closeTips}>
                <DialogTitle>Hello CodeSandbox</DialogTitle>
                <DialogContent>Start editing to see some magic happen!</DialogContent>
            </Dialog>
            <br/>
            <br/>
            <br/>
            <CountryPicker handleCountryChange={handleCountryChange}/>
            <Chart data={data} country={country}/>
            <h4>Created by Jamal Dabas</h4>
        </div>
    );
}

export default App;
